mapper.insert:

INSERT INTO BOARD(
               SEQ
              , TITLE
              , WRITER
              , CONTENT
              , CATEGORY
             )
        VALUES(
                 (SELECT IFNULL(MAX(SEQ), 0) + 1 FROM BOARD A)
                , #{title}
                , #{writer}
                , #{content}
                , #{category}
              )

mapper.update:

UPDATE BOARD
   SET 
      TITLE = #{title}
     , CONTENT = #{content}

      WHERE SEQ = #{seq}

mapper.select:
SELECT * FROM BOARD_FILE
   WHERE SEQ = #{seq}
    AND F_SEQ = (SELECT MAX(F_SEQ) FROM BOARD_FILE A WHERE SEQ = #{seq})

mapper.delete:
DELETE FROM BOARD_FILE
   WHERE SEQ = #{seq}
     AND F_SEQ = #{fSeq}

mapper.insert:

INSERT INTO freeboard_comment(
               FM_SEQ
              , SEQ
              , FM_COMMENT
              , FM_NICKNAME
             )
        VALUES(   
                  (SELECT IFNULL (MAX(FM_SEQ), 0) + 1 FROM FREEBOARD_COMMENT A)
                ,   #{seq}
                , #{fmComment}
                , #{fmNickname}
              )
