mapper.insert:


 INSERT INTO PLACE( 
               P_SEQ
               , P_CATEGORY
               , P_AREA
               , P_NAME
               , P_ADDRESS
               , P_TELL
               , P_INFO
               , P_IMG_PATH
               , P_X_AXIS
               , P_Y_AXIS
               , ORIGINALFILENAME
               , FILEPATH
               )

VALUES(
               (SELECT IFNULL(MAX(P_SEQ), 0) + 1 FROM PLACE A)
                , #{pCategory}
                , #{pArea}
                , #{pName}
                , #{pAddress}
                , #{pTell}
                , #{pInfo}
                , #{pImgpath}
                , #{pXaxis}
                , #{pYaxis}
                , #{originalFileName}
                , #{filePath})


INSERT INTO REVIEW_CM(
               RC_SEQ
              , M_SEQ
              , P_SEQ
              , BAB
              , CLEAN
              , KIND
              , SWIM
              , PARKING
              , REVIEW
              , RC_TIME
             )
        VALUES(
                 (SELECT IFNULL(MAX(RC_SEQ), 0) + 1 FROM REVIEW_CM A)
                , #{mSeq}
                , #{pSeq}
                , #{bab}
                , #{clean}
                , #{kind}
                , #{swim}
                , #{parking}
                , #{review}
                , now()
              )

mapper.update:

UPDATE place
SET
originalFileName = #{originalFileName},
filePath = #{filePath}
WHERE `P_SEQ` = #{pSeq};
