mapper.insert:

INSERT INTO TRIP(
               TR_SEQ
              , TR_NAME                    
              , TR_MODE
              , TR_AREA_SET
              , TR_DATE_SET
              , TR_GENDER_SET
              , TR_PERSONNEL_SET
              , TR_LICENSE_SET
              , TR_PASSWORD_SET
              , TR_TRANSPORTATION_SET
              , TR_INTRO
              , TR_IMG_NAME
              , TR_IMG_PATH
             )
        VALUES(
                 (SELECT IFNULL(MAX(TR_SEQ), 0) + 1 FROM TRIP A)
                , #{trName}
                , #{trMode}
                , #{trAreaSet}
                , #{trDateSet}
                , #{trGenderSet}
                , #{trPersonnelSet}
                , #{trLicenseSet}
                , #{trPasswordSet}
                , #{trTransportationSet}
                , #{trIntro}
                , #{trImgName}
                , #{trImgPath}
              )

mapper.delete:
DELETE FROM TRIP_FILE
WHERE SEQ = #{seq}
AND F_SEQ = #{fSeq}

mapper.insert:
INSERT INTO COMMENT(
CM_SEQ
, TR_SEQ
, M_SEQ
,CM_COMENT)
VALUES(
(SELECT IFNULL(MAX(CM_SEQ), 0) + 1 FROM COMMENT A)
, #{trSeq}
, #{mSeq}
, #{cmComent}
);
