mapper.insert:

INSERT INTO TRIP(
					TR_SEQ
				  , TR_NAME						  
				  , TR_MODE
				  , TR_AREA_SET
				  , TR_DATE_SET
				  , TR_GENDER_SET
				  , TR_PERSONNEL_SET
				  , TR_LICENSE_SET
				  , TR_PASSWORD_SET
				  ,	TR_TRANSPORTATION_SET
				  , TR_INTRO
				 )
		  VALUES(
		  			(SELECT IFNULL(MAX(TR_SEQ), 0) + 1 FROM TRIP A)
		  		  , #{trName}
		  		  , #{trMode}
		  		  , #{trAreaSet}
		  		  , #{trDateSet}
		  		  , #{trGenderSet}
		  		  , #{trPersonnelSet}
		  		  , #{trLicenseSet}
		  		  , #{trPasswordSet}
		  		  , #{trTransportationSet}
		  		  , #{trIntro}
		  		)

mapper.delete:
DELETE FROM TRIP_FILE
WHERE SEQ = #{seq}
AND F_SEQ = #{fSeq}
