mapper.select:

SELECT *
  FROM MEMBER
 	WHERE M_ID = #{mId}
   		AND M_PASSWORD = #{mPassword}
   			AND M_ACCOUNT_STATUS < 3

	 SELECT COUNT(*) FROM MEMBER
 				WHERE M_ID= #{mPassword}
					 AND M_PASSWORD =#{mPassword}

mapper.insert:

INSERT INTO MEMBER(
					M_SEQ
				  , M_ID
				  , M_NICKNAME
				  , M_PASSWORD
				  , M_NAME
				  , M_BIRTHDAY
				  , M_EMAIL
				  , M_GENDER
				  , M_TELL
				  , M_IMG_PATH
				  , M_IMG_NAME
				  , M_LICENSE
				  , M_INTRODUCE

				 )
		  VALUES(
		  			(SELECT IFNULL(MAX(M_SEQ), 0) + 1 FROM MEMBER A)
		  		  , #{mId}
		  		  , #{mNickname}
		  		  , #{mPassword}
		  		  , #{mName}
		  		  , #{mBirthday}
		  		  , #{mEmail}
		  		  , #{mGender}
		  		  , #{mTell}
		  		  , #{mImgPath}
		  		  , #{mImgName}
		  		  , #{mLicense}  
		  		  , #{mIntroduce}

		  		)

mapper.update:

UPDATE MEMBER
	SET 
		M_NICKNAME = #{mNickname}
	  , M_EMAIL = #{mEmail}
	  , M_PASSWORD = #{mPassword}
	  , M_TELL = #{mTell}
	  , M_INTRODUCE = #{mIntroduce}
	  , M_LICENSE = #{mLicense}
	  WHERE M_ID = #{mId}
