mapper.insert:


 INSERT INTO PLACE( 
					P_SEQ
					, P_CATEGORY
					, P_AREA
					, P_NAME
					, P_ADDRESS
					, P_TELL
					, P_INFO
					, P_IMG_PATH
					, P_X_AXIS
					, P_Y_AXIS
					)

VALUES(
					(SELECT IFNULL(MAX(P_SEQ), 0) + 1 FROM PLACE A)
					 , #{pCategory}
					 , #{pArea}
					 , #{pName}
					 , #{pAddress}
					 , #{pTell}
					 , #{pInfo}
					 , #{pImgpath}
					 , #{pXaxis}
					 , #{pYaxis})

mapper.delete:
DELETE FROM PLACE_FILE
	WHERE SEQ = #{seq}
	  AND F_SEQ = 1
